import React from 'react';
import Sidebar from '../sidebar/sidebar';
import {connect} from   'react-redux';
import { Field, reduxForm } from 'redux-form';
const Main = (props)=> {
    
    const getTaskFromUser = (value) => {
           console.log(value);
            
    }
   return (
       <div>
           <div >
                <button> create tesk</button>
           </div>
           <TaskFormRedux onSubmit={getTaskFromUser} />
           <Sidebar />
       </div>
    )
}




const Task = (props)=> {
    
   return (
       <div>
           <div>create task</div>
         <form  onSubmit = {props.handleSubmit}>
             <div className="container">
                   <div className="container-area">
                        <div>
                            enter a short desctiption 
                        </div>
                        <Field type='textarea'   rows="5" cols="80"  name='descValue'  placeholder='Describe what you see' />
                        <div>max letters count 256</div>
                   </div> 
                   <div className="container-info">
                       <div>
                           <p> бесплатно </p>
                           
                       </div>                  
                       
                   </div>
             </div>
            <div className=''>
              <button className=''>Done</button>
            </div>
        </form>
       </div>
    )
}


const TaskFormRedux = reduxForm({
    form:'descriptionTask'
  })(Task)


export default Main;
