import React, {useEffect} from 'react';
import h from '../history/history.module.css'
       

const MyList = (props) => {
       let reverseArrayOfMyTask = props.myListTask.reverse();
       let newArr =  reverseArrayOfMyTask.sort(function(a,b){return a._id < b._id ? -1 : 1;}).reduce(function(arr, el){
              if(!arr.length || arr[arr.length - 1]._id != el._id) {
                  arr.push(el);
              }
              return arr;
          }, []);
          console.log(newArr);

      const handleChange = () => {
              props.myTaskId.forEach((element, index )=> {
                     props.getMyListTask(props.myTaskId[index]);    
                                      
              });

       }

       /* можно переписать все через функкомноненту нужно делать обработчик и почсле того покгда сработал он, пришли пропсы их отфильтровать и отобразить!"*/ 
    const handleSubmit = (event) => {
              event.preventDefault();
       }

       let  showMyTask =   newArr.map( el => {
              return <MyListItems removeTaskUser={props.removeTaskUser}   el={el}/>
       })
           return (
 
               <div>
                      <form onSubmit={handleSubmit}>
                            <input type="submit" onClick={handleChange} /> 
                      </form>
                       {showMyTask}
                     {!props.myTaskId.length && <div>Список дел пуст</div>}
             </div>
               )
 
}








const MyListItems = (props) => {

       const closeTask = () => {
            //  props.removeTaskUser(props._id);
             console.log(props.el._id);
       }
       return (
         <div className={h.container}>
              <div className={h.id__container}>
                   <div>
                     <h3>Моя заявки</h3>
                     <h3>{props.el._id}</h3>
                   </div>
                   <div>
                          <button className={h.close_task} onClick={closeTask}>Завершить</button>
                   </div>
                     
              </div>
              <div className={h.table__container}>
                <div className={h.table_items}>
                     <table  border-buttom="1">
                            <caption>Закажчик</caption>                
                            <tr>
                                   <th>Имя</th>
                                   <td>{props.el.name}</td>
                            </tr>
                            <tr>
                                   <th>Телефон</th>
                                   <td>{props.el.phoneClient}</td>        
                            </tr>
                            <tr>
                                   <th>Город</th>
                                   <td>{props.el.sity}</td>        
                            </tr>
                            <tr>
                                   <th>Вознаграждение</th>
                                   <td>{props.el.checkPay}</td>        
                            </tr>

                     </table >
                     <div className={h.client__description}>
                            <p>desc</p>
                            <p>{props.el.desctiption}</p>
                     </div>
               </div>
       
              <div className={h.table_items}>
              <table border-buttom="1">
                            <caption>Исполнитель</caption>
                     <tr>
                            <th>Имя</th>
                            <td>{props.el.nameExecutor}</td>
                     </tr>
                     <tr>
                            <th>Фамилия</th>
                            <td>{props.el.surnameExecutor}</td>        
                     </tr>
                     <tr>
                            <th>Телефон</th>
                            <td>{props.el.phoneExecutor}</td>        
                     </tr>
                     <tr>
                            <th>Период выполнения</th>
                            <td>{props.el.executionTime}</td>        
                     </tr>
                     <div className={h.client__description}>
                          <p>desc</p>
                          <p>{props.el.commentExecutor}</p>
                      </div>

               </table>
              </div>
          </div>
        </div>  
       )
}
export default MyList;
