const POST_USER_TASK = "POST_USER_TASK"; 
let initialState = {
  newTask:[]
};
const taskReducer = (state = initialState, action) => {
    switch (action.type) {
      case  POST_USER_TASK: 
          let createTask = {
            task:action.data
          }
      return {
       ...state,
            newTask: [...state.newTask, createTask],
      }
            default:
          return state;
    }
}



 const setUserTask = (data) => {
    type: POST_USER_TASK,
    data
 }


