import React, {useState} from 'react';
import t from '../main/main.module.css/main.module.css'
import TakeTaskModalWindowRedux from '../takeTaskDo/takeTaskModalWindow'
const Paginator = (props) =>  {
    console.log(props);
    const [isActive, setActive] = useState(false);
    let pagesCount = Math.ceil(props.totalUsersCount / props.page);
    let pages = [];
    let portionSize = 10;
    for (let i = 0; i <= pagesCount; i++) {
        pages.push(i);
    }
    let portionCount = Math.ceil(pagesCount/portionSize);
    let [portionNumber, setPortionNumber] = useState(1);
    let leftPortionNumber = (portionNumber -1) * portionSize +1;
    let rigthPortionNumber = portionNumber  * portionSize;
    console.log(props.page);

    const [isTake, setTake] = useState(false);   
    const takeTaskToDo = () => {
        setTake(true)
    }

    const collectExecutorInformation = (value) => {
        console.log(value);
        props.appointAnExecutor(value.nameExecutor, value.surnameExecutor, value.phoneExecutor, value.executionTime, value.commentExecutor, props.taskList.id)
        console.log(value.nameExecutor, value.surnameExecutor, value.phoneExecutor, value.executionTime, value.commentExecutor, props.taskList.id);
        
        setTake(false)
        
    }
    const canceltakeTaskToDo = () => {
        setTake(false)
    }

        

    const openCteatorTask  = () => {
        setActive(true)
    }
    const getTaskFromUser = (value) => {
           console.log(value); 
           props.createTask(value.name, value.descValue, value.checkbo, value.sity, value.phone);
           setActive(false)        
    }
    const closeTaskWingow = () => {
        setActive(false)
        
    }
     return (
        <div>
        {props.users.map(u =>
          <div class=''>
                       <>
       {isTake && <TakeTaskModalWindowRedux  onSubmit={collectExecutorInformation} canceltakeTaskToDo={canceltakeTaskToDo} /> }
       <div className={t.task__container}>
           <div className={t.container__info}>
                <div className={t.info_left_side}>
                    <div className={t.items}> 
                        <p className={t.items_color}>Name</p>
                        <p >{u.name}</p>      
                    </div>
                    <div className={t.items}>
                        <p className={t.items_color}>Sity</p>
                        <p >{u.sity}</p>
                    </div>
                    <div className={t.items}>
                        <p className={t.items_color}>Phone</p>
                        <p >{u.phone}</p>   
                    </div>
                </div>
                <div className={t.info_right_side}>
                    <div>
                        <button onClick={takeTaskToDo}>Take it</button>
                    </div>
                    <div className={t.items}>
                        <p className={t.items_color}>Status</p>
                        <p >not take</p>
                    </div>
                    <div>
                        {u.payCheck? <div className={t.check__pay_color}> Платно</div>: <div>Бесплатно</div> }
                    </div>
                </div>

           </div>
           <div className={t.container__input}>
                 <h3 className={t.input__title}>Description</h3>
                <p className={t.input__text}>{props.taskList.descValue}</p>
           </div>
       </div>
    </>
         </div>
      )}
      <div className=''>
        {portionNumber > 1 &&
          <button className='' onClick={ () => { setPortionNumber(portionNumber-1) }}></button>}
        {pages.filter(p => p>=leftPortionNumber && p<= rigthPortionNumber).map(el =>{
            return   <span className={props.pageCount === el }
            onClick = {() => {props.getItems(el)}}>{el}</span>
        })}
        {portionCount> portionNumber &&
      <button  className='' onClick = {()=> { setPortionNumber(portionNumber+ 1) }}> </button> }
      </div>
    </div>
  )
  }

export default Paginator;
