import React, {useState} from 'react';
import Sidebar from '../sidebar/sidebar';
import {connect} from   'react-redux';
import { Field, reduxForm } from 'redux-form';
import TaskList  from './taskList';
import ItemsContainer from '../Items/items_container'
import main from './main.module.css/main.module.css'
const Main = (props)=> {
    console.log(props);
    
    const [isActive, setActive] = useState(false);
    const openCteatorTask  = () => {
        setActive(true)
    }
    const getTaskFromUser = (value) => {
           props.createTask(value.name, value.descValue, value.checkbo, value.sity, value.phone);
           setActive(false)        
    }
    const closeTaskWingow = () => {
        setActive(false)
        
    }
    const renderTask = props.task.map(el => {
        return <TaskList appointAnExecutor={props.appointAnExecutor} taskList = {el} />
    })
   return (
       <div>
           {  isActive  && <TaskFormRedux onSubmit={getTaskFromUser} closeTaskWingow={closeTaskWingow} />}
           <div className={main.container__button} >
                <button className={main.button} onClick = {openCteatorTask}> create tesk</button>
           </div>
                    <ItemsContainer   />
                   <Sidebar />
       </div>
    )
}




const Task = (props)=> {   
   return (
       <div>
           <div>create task</div>
         <form  className={main.form__createtask} onSubmit = {props.handleSubmit}>
             <div className="container">
                   <div className="container-area">
                        <div>
                            enter a short desctiption 
                        </div>
                        <Field component='Textarea'   rows="5" cols="80"  class={main.message} name='descValue'  placeholder='Describe what you see' />
                        <div>max letters count 256</div>
                   </div> 
                   <div className="container-info">
                       <div>
                       <Field component='Input'   class={main.textbox}name='name' placeholder=' enter your name' />   
                           <p> бесплатно </p>
                           <Field component='input' type="checkbox"  name='checkbo'  />   
                       </div>                  
                        <Field component='Input'  class={main.textbox}  name='sity' placeholder='введите ваш город' />   
                        <Field component='Input'   class={main.textbox}  name='phone' placeholder='введите ваш номер леефона' />   
                   </div>
             </div>
            <div className=''>
            <button className=''  class={main.button} >done</button>
              <button className='' class={main.button} onClick={props.closeTaskWingow}>exit</button>
            </div>
        </form>
       </div>
    )
}



const TaskFormRedux = reduxForm({
    form:'descriptionTask'
  })(Task)


export default Main;
