import {putTask} from '../api/api'
const POST_USER_TASK = "POST_USER_TASK"; 
let initialState = {
  newTask:[]
};
const taskReducer = (state = initialState, action) => {
    switch (action.type) {
      case  POST_USER_TASK: 
          let task = {
            name: action.name,
            descValue: action.desctiption,
            checkbo: action.checkPay,
            sity: action.sity,
            phone: action.phone,
            id: action._id
          } 

      return {
       ...state,
            newTask: [...state.newTask, task],
      }
            default:
          return state;
    }
}



 const setUserTask = (name, desctiption, checkPay, sity, phone, _id) => {
   
   return {
     type: POST_USER_TASK,
     name,
     desctiption,
     checkPay,
     sity,
     phone,
     _id
   }
 }



export const createTask = (name, descValue, checkbo, sity, phone) => {
  return  async (dispatch) => {
   let data = await putTask.createNewTask(name, descValue, checkbo, sity, phone );
   console.log(data); 
   dispatch(setUserTask(data.name, data.desctiption, data.checkPay, data.sity, data.phoneClient, data._id));
  }
}


export const appointAnExecutor = (nameExecutor, surNameExecutor, phoneExecutor, executionTime, commecntExecutor, id) => {
  return  async (dispatch) => {
   let data = await putTask.putTaskForMake(nameExecutor, surNameExecutor, phoneExecutor, executionTime, commecntExecutor, id );
   console.log(data); 
//   dispatch(setFullTask(data));
  }
}

export default taskReducer;
