import React, {useState} from 'react';
import Sidebar from '../sidebar/sidebar';
import {connect} from   'react-redux';
import { Field, reduxForm } from 'redux-form';
const Main = (props)=> {
    const [isActive, setActive] = useState(false);
    const openCteatorTask  = () => {
        setActive(true)
    }
    const getTaskFromUser = (value) => {
           console.log(value); 
           props.createTask(value.name, value.descValue, value.checkbo, value.sity, value.phone);
           setActive(false)        
    }
    const closeTaskWingow = () => {
        setActive(false)
        
    }
   return (
       <div>
           {  isActive  && <TaskFormRedux onSubmit={getTaskFromUser} closeTaskWingow={closeTaskWingow} />}
           <div >
                <button onClick = {openCteatorTask}> create tesk</button>
           </div>

           <Sidebar />
       </div>
    )
}




const Task = (props)=> {   
   return (
       <div>
           <div>create task</div>
         <form  onSubmit = {props.handleSubmit}>
             <div className="container">
                   <div className="container-area">
                        <div>
                            enter a short desctiption 
                        </div>
                        <Field component='Textarea'   rows="5" cols="80"  name='descValue'  placeholder='Describe what you see' />
                        <div>max letters count 256</div>
                   </div> 
                   <div className="container-info">
                       <div>
                       <Field component='Input'   name='name' placeholder=' enter your name' />   
                           <p> бесплатно </p>
                           <Field component='checkbox' type="checkbox"  name='checkbo'  />   
                       </div>                  
                        <Field component='Input'   name='sity' placeholder='введите ваш город' />   
                        <Field component='Input'   name='phone' placeholder='введите ваш номер леефона' />   
                   </div>
             </div>
            <div className=''>
            <button className='' >done</button>
              <button className='' onClick={props.closeTaskWingow}>exit</button>
            </div>
        </form>
       </div>
    )
}


const TaskFormRedux = reduxForm({
    form:'descriptionTask'
  })(Task)


export default Main;
