import React from 'react';
import {connect} from   'react-redux';
import taskReducer from '../../redux/task_reducer'
import MyList from './mylist';
import { getMyListTask} from '../../redux/task_reducer'
let mapStateToProps = (state) => {
  return {
    myTask:state.task.myTask,
    myTaskId:state.task.myTaskId
  }
}

const MyListContainer = connect (mapStateToProps, {getMyListTask})(MyList);
export default MyListContainer;
