import React, {useEffect} from 'react';
import h from '../history/history.module.css'
       

class MyList extends  React.Component  {
       constructor(props) {
              super(props);
              console.log(props)
       }

       newArr =  this.props.myListTask.sort(function(a,b){return a._id < b._id ? -1 : 1;}).reduce(function(arr, el){
              if(!arr.length || arr[arr.length - 1]._id != el._id) {
                  arr.push(el);
              }
              return arr;
          }, []);

          state = {list:this.newArr};
          componentWillReceiveProps(nextProps) {
              if(nextProps.myProp !== this.props.myProps) {
                // nextProps.myProp имеет другое значение, чем наше текущее myProps
                // поэтому мы можем что-нибудь рассчитать базируясь на новом значении.
              }
            }
       componentDidMount() {
              console.log(this.newArr)
              this.showMyTask =   this.state.list.map( el => {
                     return <MyListItems   el={el}/>
              })

       }    
       sendProps() {
           
                     this.props.getMyListTask(this.props.myTaskId[1]);    
       
              console.log("sadasd")
       }
       handleSubmit(event) {
              event.preventDefault();
       }
 
       

        render() {
           return (
 
               <div>
                      <form onSubmit={this.handleSubmit}>
                            <input type="submit" onClick={this.sendProps} /> 
                      </form>
                       {this.state.list? this.showMyTask: <div> loading</div>  }
                     {!this.props.myTaskId.length && <div>Список дел пуст</div>}
             </div>
               )

     }

 
}








const MyListItems = (props) => {
       return (
         <div className={h.container}>
              <div className={h.id__container}>
                     <h3>Моя заявки</h3>
                     <h3>{props.el._id}</h3>
              </div>
              <div className={h.table__container}>
                <div className={h.table_items}>
                     <table  border-buttom="1">
                            <caption>Закажчик</caption>                
                            <tr>
                                   <th>Имя</th>
                                   <td>{props.el.name}</td>
                            </tr>
                            <tr>
                                   <th>Телефон</th>
                                   <td>{props.el.phoneClient}</td>        
                            </tr>
                            <tr>
                                   <th>Город</th>
                                   <td>{props.el.sity}</td>        
                            </tr>
                            <tr>
                                   <th>Вознаграждение</th>
                                   <td>{props.el.checkPay}</td>        
                            </tr>

                     </table >
                     <div className={h.client__description}>
                            <p>desc</p>
                            <p>{props.el.desctiption}</p>
                     </div>
               </div>
       
              <div className={h.table_items}>
              <table border-buttom="1">
                            <caption>Исполнитель</caption>
                     <tr>
                            <th>Имя</th>
                            <td>{props.el.nameExecutor}</td>
                     </tr>
                     <tr>
                            <th>Фамилия</th>
                            <td>{props.el.surnameExecutor}</td>        
                     </tr>
                     <tr>
                            <th>Телефон</th>
                            <td>{props.el.phoneExecutor}</td>        
                     </tr>
                     <tr>
                            <th>Период выполнения</th>
                            <td>{props.el.executionTime}</td>        
                     </tr>
                     <div className={h.client__description}>
                          <p>desc</p>
                          <p>{props.el.commentExecutor}</p>
                      </div>

               </table>
              </div>
          </div>
        </div>  
       )
}
export default MyList;
